import React, { Component } from 'react';
import './BurgerStyle.css';

export default class Burger extends Component {
    state = {
        lettuce: 0,
        tomato: 0,
        cheese: 0,
        meat: 0,
        lettuceRs:0,
        tomatoRs:0,
        cheeseRs:0,
        meetRs:0,
        total:0
    }
    lettuceAddIngredent = () => {
        this.setState({
            lettuce: this.state.lettuce + 1,
            lettuceRs: this.state.lettuceRs + 25
        })
    }
    lettuceRemoveIngredent = () => {
        if (this.state.lettuce >= 0) {
            this.setState({
                lettuce: this.state.lettuce - 1,
                lettuceRs: this.state.lettuceRs - 25
            })
        } else {
           this.setState.lettuce(0)
        }
    }
    tomatoAddIngredent = () => {
        this.setState({
            tomato: this.state.tomato + 1,
            tomatoRs: this.state.tomatoRs + 10
        })
    }
    tomatoRemoveIngredent = () => {
        if (this.state.tomato >= 0) {
            this.setState({
                tomato: this.state.tomato - 1,
                tomatoRs: this.state.tomatoRs - 10
            })
        } else {
            this.setState.lettuce(0)
        }
    }
    cheeseAddIngredent = () => {
        this.setState({
            cheese: this.state.cheese + 1,
            cheeseRs: this.state.cheeseRs + 15
        })
    }
    cheeseRemoveIngredent = () => {
        if (this.state.cheese >= 0) {
            this.setState({
                cheese: this.state.cheese - 1,
                cheeseRs: this.state.cheeseRs - 15
            })
        } else {
            this.setState.cheese(0)
        }
    }
    meatAddIngredent = () => {
        this.setState({
            meat: this.state.meat + 1,
            meetRs: this.state.meetRs + 50
        })
    }
    meatRemoveIngredent = () => {
        if (this.state.meat >= 0) {
            this.setState({
                meat: this.state.meat - 1,
                meetRs: this.state.meetRs - 50
            })
        } else {
            this.setState.meat(0)
        }
    }
    burgerContentRs=()=>{
        this.setState({
            total:parseInt(this.state.lettuceRs)+parseInt(this.state.tomatoRs)
        })
    }
    burgerContent = () => {
        let burger = [];
        // outputting the lettuce
        for (let i = 0; i < this.state.lettuce; i++) {
            burger.push(<div key={burger.length} className="lettuseSide"></div>);
        }
        // outputting the tomato
        for (let i = 0; i < this.state.tomato; i++){
            burger.push(<div key={burger.length} className="tomatoSide"></div>);
        }
        // outputting the cheese
        for (let i = 0; i < this.state.cheese; i++){
            burger.push(<div key={burger.length} className="cheeseSide"></div>);
        }
        // outputting the meat
        for (let i = 0; i < this.state.meat; i++){
            burger.push(<div key={burger.length} className="meatSide"></div>);
        }
        if (burger.length === 0)
            burger.push(<p key="0">Please start adding ingredients!</p>);
        return burger;
    }

    render() {
        return (
            <>
                <div className="burgerIngredients">
                    <div className="topSide"></div>
                    {this.burgerContent()}
                    <div className="bottomSide"></div>
                </div>
                <div className="ingredientsBlock">
                <p>Rs.</p>
                    <div className="ingrBtns">{this.state.lettuceRs}
                    </div>
                    <p>Lettuce</p>
                    <div className="ingrBtns">
                        <button className="ingrBtn" onClick={() => this.lettuceAddIngredent()} >Add</button>
                        <button className="ingrBtn" onClick={() => this.lettuceRemoveIngredent()}>Remove</button>
                    </div>
                    <p>TOMATO</p>
                    <div className="ingrBtns">
                        <button className="ingrBtn" onClick={() => this.tomatoAddIngredent()} >Add</button>
                        <button className="ingrBtn" onClick={() => this.tomatoRemoveIngredent()}>Remove</button>
                    </div>
                    <p>CHEESE</p>
                    <div className="ingrBtns">
                        <button className="ingrBtn" onClick={() => this.cheeseAddIngredent()}>Add</button>
                        <button className="ingrBtn" onClick={() => this.cheeseRemoveIngredent()}>Remove</button>
                    </div>
                    <p>MEAT</p>
                    <div className="ingrBtns">
                        <button className="ingrBtn" onClick={() => this.meatAddIngredent()}>Add</button>
                        <button className="ingrBtn" onClick={() => this.meatRemoveIngredent()}>Remove</button>
                    </div>
                </div>
            </>
        );
    }
}
