import React, { Component } from 'react';
import './BurgerStyle.css';

export default class Burger extends Component {
    state = {
        lettuce: 0,
        tomato: 0,
        cheese: 0,
        meat: 0
    }
lettuceAddIngredent=()=>{
    this.setState({
        lettuce:this.state.lettuce+1
    })
}
lettuceRemoveIngredent=()=>{
    this.setState({
        lettuce:this.state.lettuce-1
    })
}


    render() {
        return (
            <>
                <div className="burgerIngredients">
                    Burger
                </div>
                <div className="ingredientsBlock">
                <p>Lettuce</p>
                    <div className="ingrBtns">
                        <button className="ingrBtn" onClick={() => this.lettuceAddIngredent}>Add</button>
                        <button className="ingrBtn" onClick={() => this.lettuceRemoveIngredent}>Remove</button>
                    </div>
                </div>
            </>
        );
    }
}
