import React, { Component } from 'react';
import './BurgerStyle.css';

export default class Burger extends Component {
    state = {
        lettuce: 0,
        tomato: 0,
        cheese: 0,
        meat: 0,
        lettuceRs: 0,
        tomatoRs: 0,
        cheeseRs: 0,
        meetRs: 0,
    }
    lettuceAddIngredent = () => {
        this.setState({
            lettuce: this.state.lettuce + 1,
            lettuceRs: this.state.lettuceRs + 25
        })
    }
    lettuceRemoveIngredent = () => {
        if (this.state.lettuce > 0) {
            this.setState({
                lettuce: this.state.lettuce - 1,
                lettuceRs: this.state.lettuceRs - 25
            })
        } else {
            this.setState.lettuce(0)
        }
    }
    tomatoAddIngredent = () => {
        this.setState({
            tomato: this.state.tomato + 1,
            tomatoRs: this.state.tomatoRs + 10
        })
    }
    tomatoRemoveIngredent = () => {
        if (this.state.tomato > 0) {
            this.setState({
                tomato: this.state.tomato - 1,
                tomatoRs: this.state.tomatoRs - 10
            })
        } else {
            this.setState.lettuce(0)
        }
    }
    cheeseAddIngredent = () => {
        this.setState({
            cheese: this.state.cheese + 1,
            cheeseRs: this.state.cheeseRs + 15
        })
    }
    cheeseRemoveIngredent = () => {
        if (this.state.cheese > 0) {
            this.setState({
                cheese: this.state.cheese - 1,
                cheeseRs: this.state.cheeseRs - 15
            })
        } else {
            this.setState.cheese(0)
        }
    }
    meatAddIngredent = () => {
        this.setState({
            meat: this.state.meat + 1,
            meetRs: this.state.meetRs + 50
        })
    }
    meatRemoveIngredent = () => {
        if (this.state.meat > 0) {
            this.setState({
                meat: this.state.meat - 1,
                meetRs: this.state.meetRs - 50
            })
        } else {
            this.setState.meat(0)
        }
    }
    burgerContent = () => {
        let burger = [];
        // outputting the lettuce
        for (let i = 0; i < this.state.lettuce; i++) {
            burger.push(<div key={burger.length} className="lettuseSide"></div>);
        }
        // outputting the tomato
        for (let i = 0; i < this.state.tomato; i++) {
            burger.push(<div key={burger.length} className="tomatoSide"></div>);
        }
        // outputting the cheese
        for (let i = 0; i < this.state.cheese; i++) {
            burger.push(<div key={burger.length} className="cheeseSide"></div>);
        }
        // outputting the meat
        for (let i = 0; i < this.state.meat; i++) {
            burger.push(<div key={burger.length} className="meatSide"></div>);
        }
        if (burger.length === 0)
            burger.push(<p key="0">Welcome to Macdonald</p>);
        return burger;
    }
    clearData = () => {
        this.setState({
            lettuce:0,
            tomato:0,
            cheese:0,
            meat:0,
            lettuceRs: 0,
            tomatoRs: 0,
            cheeseRs: 0,
            meetRs: 0,
            dialogBoxOpen:false
        })
    }
    render() {
        const rupess = [this.state.lettuceRs, this.state.tomatoRs, this.state.cheeseRs, this.state.meetRs]
        const sum = rupess.reduce((result, number) => result + number);
        return (
            <>
                <div className="burgerIngredients">
                    <div className="topSide"></div>
                    {this.burgerContent()}
                    <div className="bottomSide"></div>
                </div>
                <div className="ingredientsBlock">
                    <div className="ingrBtns">
                        <p></p>
                        <p>â‚¹</p>
                    </div>
                    <div className="ingrBtns">
                        <button className="ingrBtn" onClick={() => this.lettuceAddIngredent()} >Lettuce +</button>
                        <button className="ingrBtn" onClick={() => this.lettuceRemoveIngredent()}>Lettuce -</button>
                        <p>{this.state.lettuceRs}</p>
                    </div>
                    <div className="ingrBtns">
                        <button className="ingrBtn" onClick={() => this.tomatoAddIngredent()} >Tomato +</button>
                        <button className="ingrBtn" onClick={() => this.tomatoRemoveIngredent()}>Tomato -</button>
                        <p>{this.state.tomatoRs}</p>
                    </div>
                    <div className="ingrBtns">
                        <button className="ingrBtn" onClick={() => this.cheeseAddIngredent()}>Cheese +</button>
                        <button className="ingrBtn" onClick={() => this.cheeseRemoveIngredent()}>Cheese -</button>
                        <p>{this.state.cheeseRs}</p>
                    </div>
                    <div className="ingrBtns">
                        <button className="ingrBtn" onClick={() => this.meatAddIngredent()}>Meat +</button>
                        <button className="ingrBtn" onClick={() => this.meatRemoveIngredent()}>Meat -</button>
                        <p>{this.state.meetRs}</p>
                    </div>
                    <div className="ingrBtns">
                        <p>Total :  </p>
                        <p>{sum}</p>
                    </div>
                    <div className="ingrBtns">
                    <button className="ingrBtn" onClick={() => this.clearData()}>Reset</button>
                    <button className="ingrBtn" >submit</button>
                    </div>
                </div>
                <div>
            <h4>Popup - GeeksforGeeks</h4>
            <Popup trigger=
                {<button> Click to open modal </button>}
                modal nested>
                {
                    close => (
                        <div className='modal'>
                            <div className='content'>
                                Welcome to GFG!!!
                            </div>
                            <div>
                                <button onClick=
                                    {() => close()}>
                                        Close modal
                                </button>
                            </div>
                        </div>
                    )
                }
            </Popup>
        </div>
            </>
        );
    }
}

